/* eslint-disable no-unused-vars */
/* eslint-disable max-len */
/* eslint-disable require-jsdoc */
import React from "react";
import "./Information.css";

export default function Information({data, nextStop}) {
  // Sort stops: if nextStop is manually assigned, put it first

  console.log("Info : ", data);
  const sortedStops = [...data].sort((a, b) => {
    if (!nextStop) return a.id - b.id;
    if (a.id === nextStop.id) return -1;
    if (b.id === nextStop.id) return 1;
    return a.id - b.id;
  });

  return (
    <div className="information">
      {sortedStops.map((item) => (
        <div key={item.id} className={`info-item ${item.Arrived ? "arrived-card" : ""}`}>
          <div className={`check-circle ${item.Arrived ? "arrived" : "not-arrived"}`}>
            {item.Arrived ? "✓" : item.id}
          </div>
          <div className="info-text">
            <a>{item.address}</a>
            {item.starting_time && <p>Time: {item.starting_time}</p>}
          </div>
        </div>
      ))}
    </div>
  );
}
